[print("\nOn the %s day of Christmas,\nMy True Love gave to me:\n" % ["first", "second", "third", "fourth", "fifth", "sixth", "seventh", "eighth", "ninth", "tenth", "eleventh", "twelfth"][i] + "\n".join([line if i != 0 else "A partridge in a pear tree" for line in ["And a partridge in a pear tree", "Two turtle doves,", "Three french hens,", "Four calling birds,", "Five gold rings,", "Six geese a-laying,", "Seven swans a-swimming,", "Eight maids a-milking,", "Nine ladies dancing,", "Ten lords a-leaping,", "Eleven pipers piping,", "Twelve drummers drumming,"][:i + 1][::-1]])) for i in range(12)]
